<?php
function utf8_to_hex($string) {
	static $chr_to_hex = array();
	if (empty($chr_to_hex)) {
		mb_internal_encoding("UTF-8");
		/*for ($i = 0; $i < 256; $i++) {
			$chr = chr($i);
			$chr_to_hex[$chr] = dechex($i);
			if (strlen($chr_to_hex[$chr]) < 2) {
				$chr_to_hex[$chr] = '0' . $chr_to_hex[$chr];
			}
		}*/
		$chr_to_hex = unserialize(base64_decode(''));
	}
	$nof_str = strlen($string);
	$return = '';
	for ($i = 0; $i < $nof_str; $i++) {
		$return .= $chr_to_hex[$string[$i]];
	}
	return $return;
}
